describe('Mini Boutique', () => {
  it('Ajout et suppression dans le panier', () => {
    cy.visit('/');

    cy.contains('Produit A').parent().find('button').click();
    cy.get('#panier-liste li').should('have.length', 1);

    cy.get('.btn-supprimer').click();
    cy.get('#panier-liste li').should('have.length', 0);
  });
});
